mapper.select:
select app_id as appId from t_app where dr = 1
and update_type = #{updateType}
order by app_id

select a.url, a.md5, a.update_type, a.context, a.versioncode, a.versionname from t_app a left join t_app_ch ac on a.app_id = ac.app_id where dr = 1 and a.versioncode > #{versionCode,jdbcType=INTEGER} and ac.soft_channel_id = #{chanId,jdbcType=INTEGER}
and a.`status` = #{status,jdbcType=INTEGER}
order by a.versioncode desc limit 1
