mapper.select:
select ad_id, soft_channel_id, create_time, update_time, `type`, app_id, dr
from t_ad_channel
where ad_id = #{adId,jdbcType=INTEGER}
AND soft_channel_id = #{softChannelId}
AND app_id = #{appId}

select ac.ad_id as adId, ac.type, sc.name, a.versionname, a.app_id as appId, sc.soft_channel_id as softChannelId from t_ad_channel ac left join t_soft_channel sc on ac.soft_channel_id = sc.soft_channel_id left join t_app a on ac.app_id = a.app_id where ac.ad_id = #{adId}
and sc.name like '%' #{name} '%'
and a.app_id = #{appId}

mapper.update:
update t_ad_channel
set type = #{type,jdbcType=TINYINT}
where ad_id = #{adId,jdbcType=INTEGER}
    AND soft_channel_id = #{softChannelId}
    AND app_id = #{appId}
