mapper.select:
select chan_nickname as chanNickname, chan_name as chanName, pro_name as proName, phone, c.extra, c.create_time as createTime, c.a_id as aId from t_channel c left join t_promoter p on c.pro_id = p.pro_id
AND chan_nickname = #{chanNickname}
AND pro_name like '%' #{proName} '%'

mapper.insert:
insert into t_channel (chan_id, chan_nickname, chan_name, 
  pro_id, a_id, create_time, 
  update_time, extra, dr
  )
values (#{chanId,jdbcType=INTEGER}, #{chanNickname,jdbcType=CHAR}, #{chanName,jdbcType=VARCHAR}, 
  #{proId,jdbcType=INTEGER}, #{aId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{extra,jdbcType=CHAR}, #{dr,jdbcType=TINYINT}
  )
